  wrapperProps: PropTypes.object,
  noDataAlertProps: PropTypes.object
};

CategoryWidget.defaultProps = {
  labels: {},
  animation: true,
  filterable: true,
  searchable: true,
  wrapperProps: {},
  noDataAlertProps: {}
};

