          country: DEFAULT_COUNTRY
        });
        if (result) {
          zoomToResult(result);
          updateMarker(result);
        }
      } catch (e) {
        handleGeocodeError(e);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const zoomToResult = (result) => {
    dispatch(
      setViewState({
        longitude: result.longitude,
        latitude: result.latitude,
        zoom: 16,
        transitionDuration: 500
      })
    );
  };

  const updateMarker = (result) => {
    dispatch(setGeocoderResult(result));
  };

  const handleGeocodeError = (error) => {
    if (props.onError) {
      props.onError(error);
    }
  };

  return (
    <Paper className={`${props.className} ${classes.paperInput}`} elevation={2}>
      {loading ? (
        <CircularProgress size={20} className={classes.icon} />
      ) : (
        <SearchIcon className={classes.icon} />
      )}
      <InputBase
        type='search'
        tabIndex={-1}
        inputRef={inputRef}
        size='small'
        placeholder='Search address'
        className={classes.input}
        value={searchText}
        inputProps={{ 'aria-label': 'GeocoderSearch' }}
        onChange={handleChange}
        onInput={handleInput}
        onKeyDown={handleKeyPress}
        onBlur={handleBlur}
      />
    </Paper>
  );
}

GeocoderWidget.propTypes = {
  className: PropTypes.string,
  onError: PropTypes.func
};

GeocoderWidget.defaultProps = {};

export default GeocoderWidget;

