  );
  const filters = useSourceFilters({ dataSource, id });

  const [timeSeriesData, setTimeSeriesData] = useState([]);
  const [selectedStepSize, setSelectedStepSize] = useState(stepSize);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (stepSize !== selectedStepSize) {
      setSelectedStepSize(stepSize);
    }
    // Only on stepSize update
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [stepSize]);

  useEffect(() => {
    setIsLoading(true);

    if (isSourceReady) {
      getTimeSeries({
        filters,
        dataSource,
        column,
        stepSize: selectedStepSize,
        operationColumn,
        operation
      })
        .then((data) => {
          if (data) {
            setIsLoading(false);
            setTimeSeriesData(data);
          }
        })
        .catch((error) => {
          setIsLoading(false);
          if (onError) onError(error);
        });
    }
  }, [
    id,
    filters,
    dataSource,
    column,
    selectedStepSize,
    isSourceReady,
    setIsLoading,
    operationColumn,
    operation,
    onError
  ]);

  const handleTimeWindowUpdate = useCallback(
    (timeWindow) => {
      if (!isLoading) {
        dispatch(
          addFilter({
            id: dataSource,
            column,
            type: FilterTypes.TIME,
            values: [timeWindow.map((date) => date.getTime?.() || date)],
            owner: id
          })
        );

        if (onTimeWindowUpdate) onTimeWindowUpdate(timeWindow);
      }
    },
    [column, dataSource, isLoading, dispatch, id, onTimeWindowUpdate]
  );

  const handleTimelineUpdate = useCallback(
    (timelinePosition) => {
      if (!isLoading) {
        const { name: moment } = timeSeriesData[timelinePosition];
        dispatch(
          addFilter({
            id: dataSource,
            column,
            type: FilterTypes.TIME,
            values: [[moment, moment + STEP_SIZE_RANGE_MAPPING[selectedStepSize]]],
            owner: id
          })
        );

        if (onTimelineUpdate) onTimelineUpdate(new Date(moment));
      }
    },
    [
      column,
      dataSource,
      isLoading,
      dispatch,
      id,
      onTimelineUpdate,
      selectedStepSize,
      timeSeriesData
    ]
  );

  const handleStop = useCallback(() => {
    dispatch(
      removeFilter({
        id: dataSource,
        column
      })
    );

    if (onStop) onStop();
  }, [column, dataSource, dispatch, onStop]);

  const [anchorEl, setAnchorEl] = useState(null);

  const handleOpenStepSizeMenu = (e) => {
    if (e?.currentTarget) {
      setAnchorEl(e.currentTarget);
    }
  };

  const handleCloseStepSizeMenu = () => {
    setAnchorEl(null);
  };

  const handleStepSizeUpdate = (stepSize) => {
    setSelectedStepSize(stepSize);
    handleCloseStepSizeMenu();
  };

  return (
    <>
      <WrapperWidgetUI
        title={title}
        isLoading={isLoading}
        {...wrapperProps}
        actions={[
          ...(wrapperProps?.actions || []),
          ...(stepSizeOptions?.length
            ? [
                {
                  id: 'a0',
                  name: 'Bucket size',
                  icon: <StepSizeIcon />,
                  action: handleOpenStepSizeMenu
                }
              ]
            : [])
        ]}
      >
        {timeSeriesData.length || isLoading ? (
          <TimeSeriesWidgetUI
            data={timeSeriesData}
            stepSize={selectedStepSize}
            chartType={chartType}
            tooltip={tooltip}
            tooltipFormatter={tooltipFormatter}
            formatter={formatter}
            height={height}
            showControls={showControls}
            animation={animation}
            isPlaying={isPlaying}
            onPlay={onPlay}
            isPaused={isPaused}
            onPause={onPause}
            onStop={handleStop}
            // timelinePosition={timelinePosition}
            onTimelineUpdate={handleTimelineUpdate}
            timeWindow={timeWindow}
            onTimeWindowUpdate={handleTimeWindowUpdate}
          />
        ) : (
          <NoDataAlert {...noDataAlertProps} />
        )}
      </WrapperWidgetUI>
      <Menu
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleCloseStepSizeMenu}
      >
        <MenuItem disabled>
          <Typography variant='caption' color='textSecondary'>
            Step size
          </Typography>
        </MenuItem>
        {stepSizeOptions.map((stepSize) => (
          <MenuItem
            key={stepSize}
            selected={selectedStepSize === stepSize}
            onClick={() => handleStepSizeUpdate(stepSize)}
          >
            {capitalize(stepSize)}
          </MenuItem>
        ))}
      </Menu>
    </>
  );
}

TimeSeriesWidget.propTypes = {
  // Widget
  id: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  dataSource: PropTypes.string.isRequired,
  column: PropTypes.string.isRequired,
